package com.tictok.RUCliente.Empleado;

import com.tictok.Commons.SuperActividadDTO;
import com.tictok.RUCliente.Main;
import com.tictok.RUCliente.MiniCuenta;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.fxml.Initializable;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.Label;
import javafx.scene.control.SplitPane;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;
import javafx.stage.Stage;

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.net.URL;
import java.util.Base64;
import java.util.ResourceBundle;
//
//@Component
public class CardActividadController implements Initializable {
    @FXML
    public SplitPane pane;
    public Label centroDeportivo;
    @FXML
    private Label costo;

    @FXML
    private Label direccionYBarrio;

    @FXML
    private ImageView imagen;

    @FXML
    private Label nombre;
    private SuperActividadDTO actividadSeleccionada;
    private MiniCuenta miniCuenta;

    public void setMiniCuenta(MiniCuenta miniCuenta) {
        this.miniCuenta = miniCuenta;
    }

    public void setDatosActividad(SuperActividadDTO actividad) {
        actividadSeleccionada = actividad;
        //imageSrc seria la src dentro de mi computadora, tendria que ser en la base

        byte[] decodedBytes;
        if (actividad.getImageString() == null) {
            decodedBytes = Base64.getDecoder().decode("");
        } else{
            decodedBytes = Base64.getDecoder().decode(actividad.getImageString());
        }
        ByteArrayInputStream i = new ByteArrayInputStream(decodedBytes);
        //Image image = new Image(getClass().getResourceAsStream(actividad.getImageString()));
        imagen.setImage(new Image(i));
        //imagen.setImage(image);

        nombre.setText(actividad.getNombreServicio());
        costo.setText("Costo: " + actividad.getPrecio());
        direccionYBarrio.setText(actividad.getAddress() +", "+ actividad.getBarrio());
        centroDeportivo.setText("Centro: "+ actividad.getNombreCentro() );

    }

    @Override
    public void initialize(URL location, ResourceBundle resources) {

    }


    public void abrirVentanaConDatos(ActionEvent actionEvent) throws IOException {

        if (actividadSeleccionada.getPaseLibre()){
            FXMLLoader fxmlLoader = new FXMLLoader();
            fxmlLoader.setControllerFactory(Main.getContext()::getBean);
            InfoActSinReservaController controller = (InfoActSinReservaController) Main.getContext().getBean("infoActSinReservaController");
            controller.setDatos(actividadSeleccionada);
            Parent root = fxmlLoader.load(CardActividadController.class.getResourceAsStream("/com/tictok/RUCliente/Empleado/infoActSinReserva.fxml"));

            //ReservarActividadController controller = fxmlLoader.getController();
            //controller.setDatos(actividadSeleccionada);


            Stage stage = new Stage();
            Scene escena = new Scene(root);
            stage.setScene(escena);
            escena.getStylesheets().add("/com/tictok/RUCliente/entidad_style.css");
            stage.show();

        }else {


            FXMLLoader fxmlLoader = new FXMLLoader();
            fxmlLoader.setControllerFactory(Main.getContext()::getBean);
            VerHorariosActividadController controller = (VerHorariosActividadController) Main.getContext().getBean("verHorariosActividadController");
            controller.setDatos(actividadSeleccionada);
            controller.setMiniCuenta(miniCuenta);
            Parent root = fxmlLoader.load(CardActividadController.class.getResourceAsStream("/com/tictok/RUCliente/Empleado/reservaActividad.fxml"));

            //ReservarActividadController controller = fxmlLoader.getController();
            //controller.setDatos(actividadSeleccionada);


            Stage stage = new Stage();
            Scene escena = new Scene(root);
            stage.setScene(escena);
            escena.getStylesheets().add("/com/tictok/RUCliente/entidad_style.css");
            stage.show();
        }
    }

}
